/* Traitement de transformation du contenu */
function xml_vers_div(contenu, etape, couleur) {
var contenu1 = contenu.replace(/\/\>/g, '[fin]');             //fin de balise  />
var contenu2 = contenu1.replace(/\<\/[\w.]+\>/g, '[fin]');    // balise fermante  </abc>
var contenu3 = contenu2.replace(/\</g, '[deb]');            // debut de balise  <
var contenu4 = contenu3.replace(/\>/g, ' : ');              // fin de balise apres le nom de l'element
var contenu5 = contenu4.replace(/\[fin\]/g, '</div>');        // fin de balise
var contenu9 = contenu5.replace(/\[deb\]/g, '<div>');         // debut de balise

var contenu_html = '<style>div{border-width:1px; border:solid; border-color:'+couleur+'; margin:1px; padding:1px;} div:hover{border-color:#100808;}</style>'+etape+contenu9;
return(contenu_html);
}


